    public static ArrayList<Long> sortArray(ArrayList<Long> array) {
        if(array.size() < 2) return array;
        // first find the index of the middle element
        int mid = array.size() / 2;
        // then we find the sum of the two middle elements
        long sum = array.get(mid) + array.get(mid - 1);
        // if the sum is odd we sort the array in ascending order
        if(sum % 2 != 0)
        {
            ArrayList<Long> asc = array;
            Collections.sort(asc);
            return asc;
        }
        // if the sum is even we sort the array in descending order
        else
        {
            ArrayList<Long> desc = array;
            Collections.sort(desc, Collections.reverseOrder());
            return desc;
        }

