                    if (array.get(j) > array.get(j + 1)) {
                        long tmp = array.get(j);
                        array.set(j, array.get(j + 1));
                        array.set(j + 1, tmp);
                    }
                } else {
                    if (array.get(j) < array.get(j + 1)) {
                        long tmp = array.get(j);
                        array.set(j, array.get(j + 1));
                        array.set(j + 1, tmp);
                    }
                }
            }
        }
        return ret;

