    public static ArrayList<Long> sortArray(ArrayList<Long> array) {
        if (array.size() < 2) {
            return array;
        }
        int i = 0, j = array.size() - 1;
        if ((array.get(0) + array.get(array.size() - 1)) % 2 == 1) {
            while (i < j) {
                while (array.get(i) % 2 == 1 && i < j) {
                    i++;
                }
                while (array.get(j) % 2 == 0 && i < j) {
                    j--;
                }
                if (i < j) {
                    array.set(i, array.get(i) + array.get(j));
                    array.set(j, array.get(i) - array.get(j));
                    array.set(i, array.get(i) - array.get(j));
                    i++;
                    j--;
                }
            }
        } else {
            while (i < j) {
                while (array.get(i) % 2 == 1 && i < j) {
                    i++;
                }
                while (array.get(j) % 2 == 0 && i < j) {
                    j--;
                }
                if (i < j) {
                    array.set(i, array.get(i) - array.get(j));
                    array.set(j, array.get(i) + array.get(j));
                    array.set(i, array.get(i) - array.get(j));
                    i++;
                    j--;
                }
            }
        }
        return array;

