        if (array.size() < 2) {
            return array;
        }
        int sum = 0;
        for (int i = 0; i < array.size(); i++) {
            sum += array.get(i);
        }
        if (sum % 2 == 1) {
            sortedArray = new ArrayList<Long>(Arrays.asList(array.get(0)));
            for (int i = 1; i < array.size(); i++) {
                for (int j = 0; j < sortedArray.size(); j++) {
                    if (array.get(i) >= sortedArray.get(j)) {
                        sortedArray.add(j, array.get(i));
                        break;
                    } else if (j == sortedArray.size() - 1) {
                        sortedArray.add(array.get(i));
                    }
                }
            }
        } else {
            sortedArray = new ArrayList<Long>(Arrays.asList(array.get(0)));
            for (int i = 1; i < array.size(); i++) {
                for (int j = 0; j < sortedArray.size(); j++) {
                    if (array.get(i) <= sortedArray.get(j)) {
                        sortedArray.add(j, array.get(i));
                        break;
                    } else if (j == sortedArray.size() - 1) {
                        sortedArray.add(array.get(i));
                    }
                }
            }
        }
        return sortedArray;

