    public static ArrayList<Long> sortArray(ArrayList<Long> array) {
        int sum = 0;
        for (long a : array) {
            sum += a;
        }
        boolean isOdd = sum % 2 == 1;
        Comparator<Long> comparator = (a, b) -> {
            if (isOdd) {
                return (int) (a - b);
            } else {
                return (int) (b - a);
            }
        };
        Collections.sort(array, comparator);
        return array;

