                    even.add(array.get(i));
                } else {
                    odd.add(array.get(i));
                }
            }
            even.sort(null);
            even = even.subList(0, even.size() - 1);
            Collections.reverse(odd);
            result.addAll(even);
            result.addAll(odd);
