    public static ArrayList<Long> sortArray(ArrayList<Long> array) {
        for (int i = 0; i < array.size() - 1; i++) {
            int sum = array.get(i) + array.get(i + 1);
            if ((sum % 2 == 0) && (array.get(i) > array.get(i + 1))) {
                Collections.swap(array, i, i + 1);
            } else if ((sum % 2 == 1) && (array.get(i) < array.get(i + 1))) {
                Collections.swap(array, i, i + 1);
            }
        }
        return array;

