        }
        if (even.size() % 2 == 0) {
            Collections.sort(even);
        } else {
            Collections.sort(even, Collections.reverseOrder());
        }
        ArrayList<Long> res = new ArrayList<Long>(odd);
        res.addAll(even);
        return res;

