    public static boolean moveOneBall(ArrayList<Long> arr) {
        if (arr.size() == 0) {
            return true;
        }
        boolean[] visited = new boolean[arr.size()];
        LinkedList<Long> queue = new LinkedList<>();
        queue.add(arr.get(0));
        while (queue.size() != 0) {
            int size = queue.size();
            for (int i = 0; i < size; i++) {
                Long v = queue.remove();
                int index = arr.indexOf(v);
                if (visited[index]) {
                    continue;
                }
                visited[index] = true;
                int indexOfNext = arr.indexOf(v + 1);
                if (indexOfNext >= 0) {
                    queue.add(arr.get(indexOfNext));
                }
            }
        }
        for (int i = 0; i < arr.size(); i++) {
            if (!visited[i]) {
                return false;
            }
        }
        return true;

