        // System.out.println("final index: " + index);
        Long toBeInserted = arr.get(index);
        arr.add(firstOccurenceIndex, toBeInserted);
        arr.remove(index);
        // System.out.println("arr after first operation: " + arr);
        // System.out.println("arr after first operation: " + arr);
        return moveOneBall(arr);

