        int N = arr.size();
        boolean[] visited = new boolean[N];
        ArrayList<ArrayList<Long>>[] adjList = new ArrayList[N];
        for (int i = 0; i < N; i++) {
            adjList[i] = new ArrayList<>();
        }
        for (int i = 0; i < N; i++) {
            for (int j = i + 1; j < N; j++) {
                if (arr.get(j) < arr.get(i)) {
                    adjList[i].add(j);
                    adjList[j].add(i);
                }
            }
        }
        int counter = 0;
        int start = 0;
