    // a string of binary number
    public static String solve(long N) {
        String binary = Long.toBinaryString(N);
        int count = 0;
        int sum = 0;
        int index = binary.length()-1;
        while(index >= 0){
            if(binary.charAt(index) == '1')
                sum += (int)Math.pow(2,count);
            index--;
            count++;
        }
        return Integer.toString(sum);

