        char[] ch = s.toCharArray();
        int count = 0;
        int i = 0;
        for (i = 0; i < ch.length; i++) {
            if (ch[i] == '0') {
                count++;
            }
            if (ch[i] == '1') {
                break;
            }
        }
        if (count == ch.length) {
            return "0";
        }
        int j = ch.length - 1;
        for (; j > i; j--) {
            if (ch[j] == '0') {
                ch[j] = '1';
                break;
            }
        }
        if (j == i) {
            return "1";
        }
        return s.substring(0, i) + "1" + s.substring(j);

