    public static String solve(long N) {
        String binary = Integer.toBinaryString(N);
        String sumBinary = "";
        int carry = 0;
        for (int i = 0; i < binary.length(); i++) {
            int digit = binary.charAt(i) - '0';
            carry += digit;
            if (carry >= 2) {
                carry = 0;
                sumBinary += "1";
            }
        }
        if (carry == 1) sumBinary += "1";
        return sumBinary;

