        ArrayList<Long> res = new ArrayList<>();
        int n = arr.size();
        for (int i = 0; i < n; i++) {
            int j = 0;
            while (j < n - 1) {
                if (arr.get((int)j) > arr.get((int)(j + 1))) {
                    Collections.swap(arr, j, j + 1);
                }
                j++;
            }
            res.add(arr.remove(n - 1));
        }
        if (res.size() > k) {
            res.subList(0, (int)k).clear();
        }
        return res;

