        PriorityQueue<Long> pq = new PriorityQueue<>(k, Collections.reverseOrder());
        for (long i : arr) {
            if (pq.size() < k) {
                pq.add(i);
            }
            else if (pq.peek() < i) {
                pq.remove();
                pq.add(i);
            }
        }
        ArrayList<Long> ans = new ArrayList<>();
        while (!pq.isEmpty()) ans.add(pq.remove());
        return ans;

