    public static ArrayList<Long> maximum(ArrayList<Long> arr, long k) {
        var res = new ArrayList<Long>();
        if (arr.size() < k) {
            return arr;
        }
        if (k == 0) {
            return new ArrayList<>();
        }
        int i = 0, j = (int) k - 1;
        while (i <= j) {
            if (arr.get(i) > arr.get(j)) {
                res.add(arr.get(i));
                i++;
            } else {
                res.add(arr.get(j));
                j--;
            }
        }
        if (i != (int) k - 1) {
            res.add(arr.get(j));
            i++;
        }
        while (i < arr.size()) {
            res.add(arr.get(i));
            i++;
        }
        return res;

