        PriorityQueue<Long> pq = new PriorityQueue<>((a, b) -> b - a);
        pq.addAll(arr.subList(0, (int)k));
        for(int i = k; i < arr.size(); i++) {
            if(arr.get(i) > pq.peek()) {
                pq.remove();
                pq.add(arr.get(i));
            }
        }
        return new ArrayList<>(pq);

