        int sum = 0;
        for (Long i: array) sum += i;
        ArrayList<Long> res = new ArrayList<Long>();
        boolean isEven = sum % 2 == 0;
        for (Long i: array) {
            if (isEven) res.add(0, i);
            else res.add(i);
        }
        return res;

