    public static ArrayList<Long> sortArray(ArrayList<Long> array) {
        int a = array.size();
        if(a < 2) return array;
        for (int i = 0; i < a; i++)
            if(array.get(i) < array.get(i + 1))
                break;
            else{
                Collections.swap(array, i, i + 1);
                i--;
            }
        return array;

