        int[][] index = new int[s][2];
        for(int i = 0; i < s; i++) {
            index[i] = new int[] {i, array.get(i).intValue()};
        }
        for(int i = 0; i < s; i++) {
            index[i][1] += i;
        }
        Arrays.sort(index, (a, b) -> Integer.compare(a[1], b[1]));
        int k = s % 2;
        if(k != 0) {
            for(int i = 0; i < s; i++) {
                result.add(Long.valueOf(index[i][0]));
            }
        } else {
            int c = s / 2 - 1;
            for(int i = c; i >= 0; i--) {
                result.add(Long.valueOf(index[i][0]));
            }
            for(int i = c + 1; i < s; i++) {
                result.add(Long.valueOf(index[i][0]));
