    // (new ArrayList<Long>(Arrays.asList((long)6l, (long)5l, (long)4l, (long)3l, (long)2l, (long)1l, (long)0l)))
    public static ArrayList<Long> sortArray(ArrayList<Long> array) {
        ArrayList<Long> arrayList = new ArrayList<Long>(array);
        if(arrayList.size() < 2){
            return arrayList;
        }
        ArrayList<Long> odd = new ArrayList<Long>();
        ArrayList<Long> even = new ArrayList<Long>();
        for(int i = 0; i < arrayList.size(); i++){
            if(i == 0){
                if(arrayList.get(0) % 2 == 0){
                    even.add(arrayList.get(0));
                }else{
                    odd.add(arrayList.get(0));
                }
            }
            else{
                if(arrayList.get(i) % 2 == 0){
                    if(arrayList.get(i - 1) % 2 == 0){
                        even.add(arrayList.get(i));
                    }else{
                        odd.add(arrayList.get(i));
                    }
                }else{
                    if(arrayList.get(i - 1) % 2 == 0){
                        odd.add(arrayList.get(i));
                    }else{
                        even.add(arrayList.get(i));
                    }
                }
            }
        }
        ArrayList<Long> sorted = new ArrayList<Long>();
        if(even.size() != 0){
            for(int i = 0; i < even.size(); i++){
                sorted.add(even.get(i));
            }
        }
        if(odd.size() != 0){
            for(int i = odd.size() - 1; i >= 0; i--){
                sorted.add(odd.get(i));
            }
        }
        return sorted;

