    // >>> f((5l))
    // (new ArrayList<Long>(Arrays.asList((long)1l, (long)2l, (long)6l, (long)24l, (long)15l)))
    public static ArrayList<Long> f(long n) {
        return IntStream.rangeClosed(1, (int)n)
            .mapToObj(i -> i % 2 == 0 ? (long)i : 1L).boxed()
            .peek(i -> i != 1).reduce(new ArrayList<>(), (al, i) -> {
                al.add(i);
                return al;
            }, (al1, al2) -> {
                al1.addAll(al2);
                return al1;
            });

