        for (char letter : message.toCharArray()) {
            char newChar = letter;
            if (Character.isLetter(letter)) {
                newChar = letter == Character.toUpperCase(letter) ? 
                    Character.toLowerCase(letter) : Character.toUpperCase(letter);
            }
            if (newChar != letter) {
                result.append(newChar);
                continue;
            }
            switch (newChar) {
                case 'a':
                case 'e':
                case 'i':
                case 'o':
                case 'u':
                case 'A':
                case 'E':
                case 'I':
                case 'O':
                case 'U':
                    newChar = (char)(newChar + 2);
                    if (newChar > 'z') {
                        newChar -= 'z' - 'a';
                    }
                    break;
            }
            result.append(newChar);
        }
        return result.toString();

