    // ("ewhjklnop")
    // >>> encrypt(("gf"))
    // ("kj")
    // >>> encrypt(("et"))
    // ("ix")
    public static String encrypt(String s) {
        String out = "";
        for (char c : s.toCharArray()) {
            if (Character.isUpperCase(c)) {
                out += (char) ((c - 'A' + 2) % 26 + 'A');
            } else if (Character.isLowerCase(c)) {
                out += (char) ((c - 'a' + 2) % 26 + 'a');
            } else {
                out += c;
            }
        }
        return out;

