    // >>> encrypt(("gf"))
    // ("kj")
    // >>> encrypt(("et"))
    // ("ix")
    public static String encrypt(String s) {
        StringBuilder sb = new StringBuilder();
        for(char c : s.toCharArray()) {
            if(c >= 'a' && c <= 'z') {
                sb.append(Character.toString((char)(c + 2)));
            } else if(c >= 'A' && c <= 'Z') {
                sb.append(Character.toString((char)(c + 2 - 26)));
            } else {
                sb.append(Character.toString(c));
            }
        }
        return sb.toString();

