            if (Character.isLowerCase(c.charAt(0))) {
                for (String k : dict.keySet()) {
                    if (Character.isUpperCase(k.charAt(0)))
                        return false;
                }
            } else {
                for (String k : dict.keySet()) {
                    if (Character.isLowerCase(k.charAt(0)))
                        return false;
                }
            }
        }
