        ArrayList<Long> evens = lst.stream().filter(x -> x % 2 == 0).collect(Collectors.toCollection(ArrayList::new));
        ArrayList<Long> odds = lst.stream().filter(x -> x % 2 != 0).collect(Collectors.toCollection(ArrayList::new));
