    public static ArrayList<Long> filterIntegers(ArrayList<Object> values) {
        ArrayList<Long> out = new ArrayList<>();
        for (Object obj : values) {
            if (obj instanceof Integer)
                out.add(((Integer) obj).longValue());
            else if (obj instanceof Long)
                out.add(((Long) obj).longValue());
            else if (obj instanceof BigInteger)
                out.add(((BigInteger) obj).longValue());
            else if (obj instanceof Float)
                out.add(((Float) obj).longValue());
            else if (obj instanceof Double)
                out.add(((Double) obj).longValue());
            else if (obj instanceof Byte)
                out.add(((Byte) obj).longValue());
            else if (obj instanceof Short)
                out.add(((Short) obj).longValue());
            else if (obj instanceof Character)
                out.add(((Character) obj).longValue());
            else if (obj instanceof BigDecimal)
                out.add(((BigDecimal) obj).longValue());
            else if (obj instanceof String && obj.toString().matches("[+-]?[0-9]+"))
                out.add(Long.parseLong(obj.toString()));
        }
        return out;

