    public static ArrayList<Long> parseMusic(String music_string) {
        ArrayList<Long> beats = new ArrayList<Long>();
        StringTokenizer tokenizer = new StringTokenizer(music_string, " ");
        while (tokenizer.hasMoreTokens())
            beats.add(tokenizer.nextToken().length() == 1 ? 4L : 2L);
        return beats;

