            else if(music_string.charAt(i) == 'o' && music_string.charAt(i+1) == '|'){
                result.add((long)2);
                i++;
            }
            else if(music_string.charAt(i) == '.' && music_string.charAt(i+1) == '|'){
                result.add((long)1);
                i++;
            }
        }
        return result;

