    // Examples:
    // >>> decimalToBinary((15l))
    // ("db1111db")
    // >>> decimalToBinary((32l))
    // ("db100000db")
    public static String decimalToBinary(long decimal) {
        StringBuffer binary = new StringBuffer();
        while(decimal != 0) {
            binary.append(decimal % 2);
            decimal = decimal / 2;
        }
        return "db" + binary.reverse() + "db";

