        // Getting the first element
        Long temp = it.next();
        // Getting a new array list
        ArrayList<Long> out = new ArrayList<>();
        // Adding the first element to the array list
        out.add(temp);
        // Iterating over the list
        while (it.hasNext()) {
            // Getting the next element
            Long temp2 = it.next();
            // If the next element is same as the previous element
            // then we skip the element
            if (temp2 == temp) {
                continue;
            }
            // Adding the element to the array list
            out.add(temp2);
            // Updating the previous element
            temp = temp2;
        }
        // Returning the result
        return out;

