    public static boolean isNested(String string) {
        Stack<String> stack = new Stack<String>();
        for (int i = 0; i < string.length(); i++) {
            if (string.charAt(i) == '[') {
                stack.push(String.valueOf(string.charAt(i)));
            } else {
                if (stack.size() == 0) {
                    return false;
                } else {
                    stack.pop();
                }
            }
        }
        return stack.size() % 2 != 0;

