        for(int i = 0; i < string.length(); i++){
            if(string.charAt(i) == '['){
                squareBracketsCounter++;
            }
            if(string.charAt(i) == ']'){
                if(squareBracketsCounter == 0){
                    return false;
                }
                squareBracketsCounter--;
            }
            if(string.charAt(i) == '('){
                parenthesisCounter++;
            }
            if(string.charAt(i) == ')'){
                if(parenthesisCounter == 0){
                    return false;
                }
                parenthesisCounter--;
            }
        }
        return squareBracketsCounter == 0 && parenthesisCounter == 0;

