    // (false)
    // >>> isNested(("[[][]]"))
    // (true)
    // >>> isNested(("[[]][["))
    // (true)
    public static boolean isNested(String string) {
        boolean[] stack = new boolean[string.length()];
        for(int i = 0; i < string.length(); i++) {
            char curr = string.charAt(i);
            if(curr == '[' && !stack[i]) {
                stack[i] = true;
            } else if(curr == ']' && stack[i]) {
                stack[i] = false;
            }
        }
        return Arrays.stream(stack).anyMatch(x -> x);

