    public static boolean isNested(String string) {
        if(string.length() == 0){
            return false;
        }
        int leftBracket = 0;
        int rightBracket = 0;
        int len = string.length();
        int index = 0;
        while(index < len){
            char character = string.charAt(index);
            if(character == '['){
                leftBracket++;
            }
            else{
                rightBracket++;
            }
            if(leftBracket == rightBracket){
                return false;
            }
            if(rightBracket > leftBracket){
                return true;
            }
            index++;
        }
        return true;

