class Problem {
    // Create a function that takes a string as input which contains only square brackets.
    // The function should return true if and only if there is a valid subsequence of brackets 
    // where at least one bracket in the subsequence is nested.
    // >>> isNested(("[[]]"))
    // (true)
    // >>> isNested(("[]]]]]]][[[[[]"))
    // (false)
    // >>> isNested(("[][]"))
    // (false)
    // >>> isNested(("[]"))
    // (false)
    // >>> isNested(("[[][]]"))
    // (true)
    // >>> isNested(("[[]][["))
    // (true)
    public static boolean isNested(String string) {
        // create a list of positions of brackets in the string
        List<Integer> positions = new ArrayList<Integer>();
        char[] chars = string.toCharArray();
        for(int i = 0; i < chars.length; i++){
            if(chars[i] == '['){
                positions.add(i);
            }else if(chars[i] == ']'){
                positions.add(i);
            }
        }

        // go through every possible sequence of brackets and see if it is valid
        for(int i = 0; i < positions.size() - 1; i++){
            for(int j = i + 1; j < positions.size(); j++){
                // if the starting bracket is not in the list, continue
                if(i % 2 == 0 && !positions.contains(i + 1)){
                    continue;
                }
                // if the ending bracket is not in the list, continue
                if(j % 2 == 1 && !positions.contains(j - 1)){
                    continue;
                }
                // if the starting bracket is followed by the ending bracket, continue
                if(positions.get(i) + 1 == positions.get(j)){
                    continue;
                }
                // if the ending bracket is preceded by the starting bracket, continue
                if(positions.get(i) - 1 == positions.get(j)){
                    continue;
                }
                // if the brackets are nested, return true
                if(positions.get(i) < positions.get(j)){
                    return true;
                }
            }
        }

        // if no brackets are nested, return false
        return false;

