        int i = arr.size() - 1;
        int k = arr.size() - 1;
        while (k > 0 && arr.get(k) < arr.get(k - 1)) {
            --k;
        }
        while (i > k && arr.get(i) < arr.get(i - 1)) {
            --i;
        }
        return i <= k;

