    public static boolean moveOneBall(ArrayList<Long> arr) {
        if (arr.size() == 0) return true;
        int N = arr.size();
        // find the first non-increasing element
        int pos = -1;
        for (int i = 0; i < N - 1; ++i) {
            if (arr.get(i) >= arr.get(i + 1)) {
                pos = i;
                break;
            }
        }
        // cannot be sorted
        if (pos < 0) {
            return false;
        }
        // has to be shifted to the front
        else if (pos == 0) {
            return true;
        }
        // try shift it forward
        long first = arr.get(0);
        ArrayList<Long> forward = new ArrayList<>(arr);
        for (int i = 1; i <= pos; ++i) {
            forward.set(i - 1, forward.get(i));
        }
        forward.set(pos, first);
        return moveOneBall(forward);

