                int j = 0;
                while (j <= length / 2 - 1) {
                    if (num.charAt(j) != num.charAt(length - 1 - j)) {
                        break;
                    }
                    j++;
                }
                if (j > length / 2 - 1) {
                    countEven++;
                }
            }
        }
        pair.set0(countEven);
        pair.set1(countOdd);
        return pair;

