    public static Pair<Long, Long> evenOddPalindrome(long n) {
        // 20170903
        if(n<10)return new Pair<>(0l, n-1l);
        else if(n<100)return new Pair<>(1l, n-2l);
        int[] arr=(n+"").chars().map(i -> i - '0').toArray();
        int s=arr.length;
        int[][] dp=new int[2][s+1];
        dp[0][1]=1;
        for(int i=0;i<2;i++){
            for(int j=2;j<=s;j++){
                if(arr[j-1]%2==i){
                    if(arr[j-1]==0)dp[i][j]=dp[i][j-1]+dp[i][j-2];
                    else dp[i][j]=dp[i][j-1];
                }
            }
        }
        return new Pair<>((long)dp[0][s], (long)dp[1][s]);

