    // Note:
    // 1. 1 <= n <= 10^3
    // 2. returned pair has the number of even and odd integer palindromes respectively.
    public static Pair<Long, Long> evenOddPalindrome(long n) {
        if (n < 1) {
            return null;
        }
        final List<Integer> evenPalindromeList = new ArrayList<>();
        final List<Integer> oddPalindromeList = new ArrayList<>();
        int evenPalindromeCount = 0;
        int oddPalindromeCount = 0;
        int min = 1;
        for (int i = 1; i <= n; i++) {
            for (int j = i; j <= n; j++) {
                final String str = String.valueOf(i) + String.valueOf(j);
                if (str.equals(new StringBuilder(str).reverse().toString())) {
                    if (i % 2 == 0) {
                        evenPalindromeList.add(i);
                    } else {
                        oddPalindromeList.add(i);
                    }
                }
            }
        }
        if (!evenPalindromeList.isEmpty()) {
            evenPalindromeCount = 1;
            for (int i = 1; i < evenPalindromeList.size(); i++) {
                if (evenPalindromeList.get(i) - evenPalindromeList.get(i - 1) != 1) {
                    evenPalindromeCount++;
                }
            }
        }
