            oddPalindromeCount = 1;
            for (int i = 1; i < oddPalindromeList.size(); i++) {
                if (oddPalindromeList.get(i) - oddPalindromeList.get(i - 1) != 1) {
                    oddPalindromeCount++;
                }
            }
        }
        return Pair.with(evenPalindromeCount, oddPalindromeCount);

