                long number = reverse(j, i);
                // System.out.println(number);
                if (number <= n) {
                    int rem = (int) (number % 10);
                    if (count.containsKey(rem)) {
                        int num = count.get(rem);
                        if (rem % 2 == 0) {
                            count.put(rem, num + 1);
                        } else {
                            count.put(rem, num + 2);
                        }
                    } else {
                        if (rem % 2 == 0) {
                            count.put(rem, 1);
                        } else {
                            count.put(rem, 2);
                        }
                    }
                }
            }
        }
        long evenCount = 0;
        long oddCount = 0;
        for (Integer key : count.keySet()) {
            int value = count.get(key);
            if (key % 2 == 0) {
                evenCount += value;
            } else {
                oddCount += value;
            }
        }
        return new Pair<>(evenCount, oddCount);

