        int odd = 0, even = 0;
        for(int i = 1; i <= n; i++){
            if(i <= 10){
                if(i % 2 == 0)
                    even++;
                else
                    odd++;
            }
            else{
                String s = String.valueOf(i);
                if(s.length() % 2 != 0){
                    continue;
                }
                boolean flag = true;
                for(int j = 0; j < s.length() / 2; j++){
                    if(s.charAt(j) != s.charAt(s.length() - j - 1))
                    {
                        flag = false;
                        break;
                    }
                }
                if(flag){
                    if(s.length() % 2 == 0)
                        even++;
                    else
                        odd++;
                }
            }
        }
        return new Pair<>(even, odd);

