                dp[i][1] = 1;
                String str = String.valueOf(i);
                int len = str.length();
                int mid = (len - 1) / 2;
                long l1 = Long.parseLong(str.substring(0, mid + 1));
                long r1 = Long.parseLong(str.substring(len - 1, len - 1 - mid));
