    public static ArrayList<Long> derivative(ArrayList<Long> xs) {
        for(int i = xs.size() - 1; i > 0; --i) {
            xs.set(i - 1, xs.get(i - 1) + xs.get(i) * (long)i);
        }
        if(xs.size() > 0) {
            xs.remove(0);
        }
        return xs;

