    public static boolean isSorted(ArrayList<Long> lst) {
        // This is to check whether there are duplicates.
        Set<Long> set = new HashSet<>();
        for(long x: lst) {
            if(!set.add(x)) {
                return false;
            }
        }
        // This checks if the array is sorted.
        for(int i = 1; i < lst.size(); i++) {
            if(lst.get(i - 1) > lst.get(i)) {
                return false;
            }
        }
