    public static ArrayList<Long> minPath(ArrayList<ArrayList<Long>> grid, long k) {
        ArrayList<ArrayList<Long>> dp = new ArrayList<ArrayList<Long>>();
        for (ArrayList<Long> row : grid) {
            ArrayList<Long> col = new ArrayList<Long>();
            for (long n : row) {
                col.add(n);
            }
            dp.add(col);
        }
        int rows = grid.size(), cols = grid.get(0).size();
        ArrayList<ArrayList<Long>> memo = new ArrayList<ArrayList<ArrayList<Long>>>();
        for (int i = 0; i < rows; i++) {
            ArrayList<ArrayList<Long>> row = new ArrayList<ArrayList<ArrayList<Long>>>();
